problem.script:
from mitxgraders import *

def heaviside(x):
    if x > 0:
        return 1
    return 0

grader = FormulaGrader(
    answers="H(x)*f''(tau)",
    user_constants={'tau': 6.2831853072},
    user_functions={
        "H": heaviside,
        "f''": RandomFunction()
    },
    variables=['x'],
    sample_from={
        'x': RealInterval([-5,5])
    }
)
