problem.script:
from mitxgraders import MatrixGrader, specify_domain, RealVectors, RealMatrices

# This is a contrived example specify_domain, just to demonstrate its usage
@specify_domain(input_shapes=[1, [2, 3], [3]], display_name='f')
def my_func(a, b, c):
    return a*b*c

# Define a grader using rotate
grader = MatrixGrader(
   answers='f(a, b, c)',
   variables=['a', 'b', 'c'],
   sample_from={
       'b': RealMatrices(shape=[2, 3]),
       'c': RealVectors(shape=3),
   },
   user_functions={
       'f': my_func
   }
)
