problem.script:
from mitxgraders import *
grader = MatrixGrader(
    variables=['nabla', 'A'],
    sample_from={
        'nabla': RealMatrices(),
        'A': RealMatrices()
    },
    max_array_dim=0,               # Students can't enter their own matrices
    suppress_matrix_messages=True  # Turns off all matrix error messages
)
